class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      baseURL : 'http://api.openweathermap.org/data/2.5/weather?',
      APIkey: '6c03ae71238b955fb2359628d62394e1',
      query: 'London',
      forecast: ''
    }
  }
  
componentDidMount() {
  //this.query()
}


query = () => {
  let url = this.state.baseURL + "q=" + this.state.query + "appid=" + this.state.APIkey
  fetch(url)
  .then(response => {
    if (response.ok)
     {console.log('success')
     return response.json
    }
  })
  .then(data => {
    let weather = data.weather[0]
    let description = data.weather[0].description
    let main = data.main
    let temp = main.temp
    let icon = weather.icon
    let sys = data.sys
    let countryCode = sys.country.toLowerCase()
    let city =  data.name
    let forecast = {temp : temp, description : description, icon : icon, code : countryCode, city: city}
    this.setState({forecast : forecast})
    console.log(forecast)
    this.setState({query: ''})
  })
}


handleChange = (e) => {
this.setState({query : e.target.value})

}


  render() {
    return (
      <div>
        
<SearchBar 
fetch={this.query} 
  change={this.handleChange}
  query={this.state.query}
/>

<Main forecast={this.state.forecast}/>


       
      </div>
    );
  }
}

export default App
