import React from 'react';
import './App.css'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      baseURL : 'http://api.openweathermap.org/data/2.5/weather?',
      APIkey: '6c03ae71238b955fb2359628d62394e1',
      query: 'London',
      forecast: ''
    }
  }
  
componentDidMount() {
  //this.query()
}


query = () => {
  let url = this.state.baseURL + "q=" + this.state.query + "appid=" + this.state.APIkey
  fetch(url)
  .then(response => {
    if (response.ok)
     {console.log('success')
     return response.json
    }
  })
  .then(data => {
    let weather = data.weather[0]
    let description = data.weather[0].description
    let main = data.main
    let temp = main.temp
    let icon = weather.icon
    let sys = data.sys
    let countryCode = sys.country.toLowerCase()
    let forecast = {temp : temp, description : description, icon : icon, code : countryCode}
    this.setState({forecast : forecast})
    console.log(forecast)
  })
}



handleChange = (e) => {
this.setState({query : e.target.va})

}


  render() {
    return (
      <div>
        <div id="current" className="wrapper">
          <nav className=" navbar-fixed-top">
            <div className="container margin-left" >
              <form className="card my-6">
                <div className="card-body row no-gutters align-items-center">

                  <div className="col">
                    <input className="form-control form-control-lg form-control-borderless" type="search"
                      placeholder="Search ..."  value={this.state.query} onChange ={(e) => this.handleChange(e)}/>
                  </div>
                </div>
              </form>
            </div>
          </nav>
        </div>

        <footer className="footer">
          <p>Weather Data from <a href="https://openweathermap.org" target="_blank">Openweathermap.org</a></p>
        </footer>
      </div>
    );
  }
}

export default App
